package week1.day2;

public class CheckIfTheNumberIsPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = -35;
		if (num > 0 ) {
			System.out.println("Number"+num+"is positive");
		}
		else if (num <0) {
			System.out.println("Number"+num+"is negative");
		}

	}

}
